module top_module(
    input clk,
    input areset,
    input train_valid,
    input train_taken,
    output logic [1:0] state
);

    localparam IDLE = 2'b00;
    localparam TAKE = 2'b01;
    localparam WAIT = 2'b10;
    localparam DONE = 2'b11;

    reg [1:0] next_state;
    reg [1:0] current_state;

    always @ (posedge clk or negedge areset) begin
        if (!areset) begin
            current_state <= IDLE;
        end else begin
            current_state <= next_state;
        end
    end

    always @ (posedge clk or negedge areset) begin
        if (!areset) begin
            next_state <= IDLE;
        end else begin
            case (current_state)
                IDLE: begin
                    if (train_taken) begin
                        next_state <= TAKE;
                    end else if (train_valid) begin
                        next_state <= IDLE;
                    end
                end
                TAKE: begin
                    if (train_taken) begin
                        next_state <= TAKE;
                    end else if (train_valid) begin
                        next_state <= WAIT;
                    end
                end
                WAIT: begin
                    if (train_taken) begin
                        next_state <= TAKE;
                    end else if (train_valid) begin
                        next_state <= WAIT;
                    end
                end
                DONE: begin
                    next_state <= DONE;
                end
            endcase
        end
    end

    assign state = current_state;

endmodule

