wire [7:0] data;
wire valid;

// FSM states
parameter IDLE = 4'b0000;
parameter WAIT = 4'b0001;
parameter PROCESS = 4'b0010;
parameter DONE = 4'b0011;

// FSM transitions
always @(posedge clock) begin
    case(state)
        IDLE: begin
            counter <= 0;
            out <= 0;
            if(reset) begin
                state <= IDLE;
            end
            else begin
                state <= WAIT;
            end
        end
        WAIT: begin
            counter <= counter + 1;
            out <= 0;
            if(counter == 10) begin
                state <= PROCESS;
            end
            else begin
                state <= WAIT;
            end
        end
        PROCESS: begin
            data <= in;
            out <= data;
            state <= DONE;
        end
        DONE: begin
            valid <= 1;
            state <= IDLE;
        end
    endcase
end

