module top_module (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    output walk_left,
    output walk_right
);

// State register
reg [1:0] state;

// Initial state
initial begin
    state = 2'b00;
end

// State update on rising edge of clk
always @(posedge clk or posedge areset) begin
    if (areset) begin
        state <= 2'b00; // Reset to initial state
    end else begin
        case (state)
            2'b00: if (bump_left) state <= 2'b01;
            2'b01: if (bump_right) state <= 2'b10;
            2'b10: if (!bump_left) state <= 2'b11;
            2'b11: if (!bump_right) state <= 2'b00;
        endcase
    end
end

// Output assignments
assign walk_left = (state == 2'b00 || state == 2'b11);
assign walk_right = (state == 2'b01 || state == 2'b10);

endmodule

