    input resetn,
    input [3:1] r,
    output [3:1] g
);

// State definition
parameter A = 2'b00, B = 2'b01, C = 2'b10, D = 2'b11;
reg [1:0] state, next_state;

// State register
always @(posedge clk or negedge resetn) begin
    if (!resetn)
        state <= A;
    else
        state <= next_state;
end

// Next state logic
always @(*) begin
    case (state)
        A: if (r[1]) next_state = B;
            else if (r[2]) next_state = C;
            else if (r[3]) next_state = D;
            else next_state = A;
        B: if (r[1]) next_state = B;
            else next_state = A;
        C: if (r[2]) next_state = C;
            else next_state = A;
        D: if (r[3]) next_state = D;
            else next_state = A;
    endcase
end

// Output logic
always @(*) begin
    case (state)
        B: g[1] = 1'b1;
        C: g[2] = 1'b1;
        D: g[3] = 1'b1;
        default: g = 3'b0;
    endcase
end

