    input clk,
    input in,
    input areset,
    output out
);

  // Define states
  parameter A = 2'b00;
  parameter B = 2'b01;
  parameter C = 2'b10;
  parameter D = 2'b11;

  // State register
  reg current_state, next_state;

  // Output register
