module top_module (
    input clk,
    input resetn,
    input x,
    input y,
    output f,
    output g
);

  parameter A = 0, B = 1, C = 2, D = 3, E = 4, F = 5, G1 = 6, G2 = 7, H = 8, I = 9;
  reg state, next_state;
  reg f_reg, g_reg;

  always @(posedge clk) begin
    if (!resetn) begin
      state <= A;
      f_reg <= 0;
      g_reg <= 0;
    end else begin
      state <= next_state;
      f_reg <= (next_state == B) ? 1 : 0;
      g_reg <= (next_state == H || next_state == I) ? g_reg : ((next_state == E) ? 1 : 0);
    end
  end

  always @(*) begin
    next_state = state;
    case (state)
      A: next_state = B;
      B: next_state = C;
      C: next_state = (x == 1) ? D : C;
      D: next_state = (x == 0) ? E : C;
      E: next_state = (x == 1) ? F : C;
      F: next_state = G1;
      G1: next_state = (y == 1) ? H : G2;
      G2: next_state = (y == 1) ? H : I;
      H: next_state = H;
      I: next_state = I;
      default: next_state = A;
    endcase
  end

  assign f = f_reg;
  assign g = g_reg;

endmodule

