module top_module (
    input clk,
    input reset,
    input w,
    output z
);

  // State encoding
  parameter A = 3'b000;
  parameter B = 3'b001;
  parameter C = 3'b010;
  parameter D = 3'b011;
  parameter E = 3'b100;
  parameter F = 3'b101;

  reg [2:0] current_state, next_state;

  // State flip-flops
  always @(posedge clk) begin
    if (reset) begin
      current_state <= A;
    end else begin
      current_state <= next_state;
    end
  end

  // State table
  always @(*) begin
    case (current_state)
      A: begin
        if (w == 1'b1) next_state = B;
        else next_state = A;
      end
      B: begin
        if (w == 1'b1) next_state = C;
        else next_state = D;
      end
      C: begin
        if (w == 1'b1) next_state = E;
        else next_state = D;
      end
      D: begin
        if (w == 1'b1) next_state = F;
        else next_state = A;
      end
      E: begin
        if (w == 1'b1) next_state = E;
        else next_state = D;
      end
      F: begin
        if (w == 1'b1) next_state = C;
        else next_state = D;
      end
      default: next_state = A; // Should not happen, but good practice
    endcase
  end

  // Output logic
  assign z = (current_state == E);

endmodule

