module top_module (
    input clk,
    input reset,
    input s,
    input w,
    output reg z
);

    reg [1:0] state;
    reg [1:0] count; // Count of ones in the last 3 cycles
    reg [1:0] cycle; // Cycle counter (0 to 2)

    localparam A = 2'b00;
    localparam B = 2'b01;
    localparam C = 2'b10;
    localparam D = 2'b11;

    always @(posedge clk) begin
        if (reset) begin
            state <= A;
            z <= 0;
            count <= 0;
            cycle <= 0;
        end else begin
            case (state)
                A: begin
                    if (s) begin
                        state <= B;
                        count <= 0;
                        cycle <= 0;
                        z <= 0;
                    end else begin
                        state <= A;
                        z <= 0;
                    end
                end
                B: begin
                    if (cycle == 2'b00) begin
                        count <= w;
                        cycle <= cycle + 1;
                        state <= C;
                        z <= 0;
                    end
                end
                C: begin
                    if (cycle == 2'b01) begin
                        count <= count + w;
                        cycle <= cycle + 1;
                        state <= D;
                        z <= 0;
                    end
                end
                D: begin
                    if (cycle == 2'b10) begin
                        count <= count + w;
                        cycle <= 0;
                        state <= B;
                        if (count == 2'b10) begin
                            z <= 1;
                        end else begin
                            z <= 0;
                        end
                    end
                end
                default: state <= A;
            endcase
        end
    end

endmodule

