module top_module (
    input clk,
    input reset,
    input in,
    output reg disc,
    output reg flag,
    output reg err
);

  reg [3:0] state;

  localparam S_IDLE  = 4'b0000;
  localparam S_ONE   = 4'b0001;
  localparam S_TWO   = 4'b0010;
  localparam S_THREE = 4'b0011;
  localparam S_FOUR  = 4'b0100;
  localparam S_FIVE  = 4'b0101;
  localparam S_SIX   = 4'b0110;
  localparam S_SEVEN = 4'b0111;

  always @(posedge clk) begin
    if (reset) begin
      state <= S_IDLE;
      disc <= 0;
      flag <= 0;
      err <= 0;
    end else begin
      disc <= 0;
      flag <= 0;
      err <= 0;

      case (state)
        S_IDLE: begin
          if (in) state <= S_ONE;
          else state <= S_IDLE;
        end
        S_ONE: begin
          if (in) state <= S_TWO;
          else state <= S_IDLE;
        end
        S_TWO: begin
          if (in) state <= S_THREE;
          else state <= S_IDLE;
        end
        S_THREE: begin
          if (in) state <= S_FOUR;
          else state <= S_IDLE;
        end
        S_FOUR: begin
          if (in) state <= S_FIVE;
          else state <= S_IDLE;
        end
        S_FIVE: begin
          if (in) state <= S_SIX;
          else state <= S_IDLE;
        end
        S_SIX: begin
          if (in) state <= S_SEVEN;
          else begin
            state <= S_IDLE;
            disc <= 1;
          end
        end
        S_SEVEN: begin
          if (in) begin
            state <= S_SEVEN;
            err <= 1;
          end else begin
            state <= S_IDLE;
            flag <= 1;
          end
        end
        default: state <= S_IDLE;
      endcase
    end
  end

endmodule

