    input wire clk,
    input wire reset,
    input wire data,
    output wire [3:0] count,
    output reg counting,
    output reg done,
    input wire ack );

    reg [3:0] state;
    reg [3:0] delay;
    reg [11:0] cycle_count;
    reg [3:0] current_count;
    reg [3:0] pattern;

    localparam IDLE = 0;
    localparam DETECTED = 1;
    localparam DELAY = 2;
    localparam COUNTING = 3;
    localparam DONE = 4;

    always @(posedge clk) begin
        if (reset) begin
            state <= IDLE;
            counting <= 0;
            done <= 0;
            cycle_count <= 0;
            current_count <= 0;
            pattern <= 0;
        end else begin
            case (state)
                IDLE: begin
                    pattern <= {pattern[2:0], data};
                    if (pattern == 4'b1101) begin
                        state <= DETECTED;
                    end
                end
                DETECTED: begin
                    delay[3] <= data;
                    state <= DELAY;
                end
                DELAY: begin
                    delay[2] <= data;
                    state <= DELAY + 1;
                end
                DELAY + 1: begin
                    delay[1] <= data;
                    state <= DELAY + 2;
