  reg [7:0] data_reg;
  reg [2:0] bit_count;
  reg done_reg;

  always @(posedge clk) begin
    if (reset) begin
      state <= IDLE;
      data_reg <= 0;
      bit_count <= 0;
      done_reg <= 0;
    end else begin
      state <= next_state;
      done_reg <= 0;
      if (state == DATA) begin
        data_reg[bit_count] <= in;
