module top_module (
    input a,
    input b,
    input c,
    input d,
    output out
);

    assign out = (~b & ~c) | (~b & ~d) | (~a & ~c & ~d) | (a & b & c) | (a & c & ~d);

endmodule

module top_module (
    input clk,
    input reset,
    input s,
    input w,
    output reg z
);

    reg [2:0] state;
    localparam A = 3'b000, B = 3'b001, C = 3'b010, D = 3'b011, E = 3'b100;
    reg [1:0] ones_count;
    reg z_next;

    always @(posedge clk) begin
        if (reset) begin
            state <= A;
            z <= 0;
        end else begin
            state <= next_state;
            z <= z_next;
        end
    end

    reg [2:0] next_state;
    always @(*) begin
        next_state = state;
        z_next = 0; // Default

        case (state)
            A: begin
                if (s) next_state = B;
                else next_state = A;
            end
            B: begin
                ones_count = w;
                next_state = C;
            end
            C: begin
                ones_count = ones_count + w;
                next_state = D;
            end
            D: begin
                ones_count = ones_count + w;
                next_state = E;
            end
            E: begin
                if (ones_count == 2) z_next = 1;
                next_state = B;
            end
            default: next_state = A;
        end
    end

endmodule

