module top_module (
    input clk,
    input areset,
    input x,
    output z
);

    // State encoding: A=2'b01, B=2'b10
    localparam A = 2'b01;
    localparam B = 2'b10;

    reg [1:0] state;
    reg z_int;

    always @(posedge clk or posedge areset) begin
        if (areset) begin
            state <= A;
        end else begin
            case (state)
                A: begin
                    if (x) state <= B;
                    else state <= A;
                end
                B: begin
                    state <= B;
                end
                default: state <= A; // Should not happen, but good practice
            endcase
        end
    end

    always @(*) begin
        case (state)
            A: begin
                if (x) z_int = 1;
                else z_int = 0;
            end
            B: begin
                if (x) z_int = 0;
                else z_int = 1;
            end
            default: z_int = 0; // Should not happen, but good practice
        endcase
    end

    assign z = z_int;

endmodule

