    case (current_state)
      A: next_state = (in == 0) ? A : B;
      B: next_state = (in == 0) ? C : B;
      C: next_state = (in == 0) ? A : D;
      D: next_state = (in == 0) ? C : B;
      default: next_state = A; // Default to state A
    endcase
  end

  // Output logic
  always @(current_state) begin
    case (current_state)
      D: out = 1;
      default: out = 0;
    endcase
  end

