module top_module (
    input clk,
    input reset,
    input s,
    input w,
    output reg z
);

    reg [2:0] state;
    parameter A = 3'b000, B = 3'b001, C1 = 3'b010, C2 = 3'b011, C3 = 3'b100, ZOUT = 3'b101;
    reg [1:0] ones_count;

    always @(posedge clk) begin
        if (reset) begin
            state <= A;
            z <= 0;
        end else begin
            case (state)
                A: begin
                    if (s) begin
                        state <= B;
                        ones_count <= 0;
                        z <= 0;
                    end else begin
                        state <= A;
                        z <= 0;
                    end
                end
                B: begin
                    state <= C1;
                    ones_count <= w;
                    z <= 0;
                end
                C1: begin
                    state <= C2;
                    ones_count <= ones_count + w;
                    z <= 0;
                end
                C2: begin
                    state <= C3;
                    ones_count <= ones_count + w;
                    z <= 0;
                end
                C3: begin
                    state <= ZOUT;
                    if (ones_count == 2) begin
                        z <= 1;
                    end else begin
                        z <= 0;
                    end
                end
                ZOUT: begin
                    state <= B;
                    z <= 0;
                end
                default: state <= A;
            endcase
            if (!s && state != A) begin
                state <= A;
                z <= 0;
            end
        end
    end

endmodule

