module top_module (
    input clk,
    input j,
    input k,
    input areset,
    output out
);

    // Define the states
    parameter OFF = 1'b0;
    parameter ON  = 1'b1;

    // State register
    reg state;

    // Output register (Moore machine)
    assign out = state;

    // State transition logic
    always @(posedge clk or posedge areset) begin
        if (areset) begin
            state <= OFF;
        end else begin
            case (state)
                OFF: begin
                    if (j) begin
                        state <= ON;
                    end else begin
                        state <= OFF;
                    end
                end
                ON: begin
                    if (k) begin
                        state <= OFF;
                    end else begin
                        state <= ON;
                    end
                end
                default: state <= OFF; // Avoid X state
            endcase
        end
    end

endmodule

