module top_module (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    input ground,
    input dig,
    output walk_left,
    output walk_right,
    output aaah,
    output digging
);

    // Define states
    localparam WALK_LEFT = 2'b00;
    localparam WALK_RIGHT = 2'b01;
    localparam FALL = 2'b10;
    localparam DIG = 2'b11;

    // State register
    reg [1:0] current_state, next_state;
    reg prev_walk_left; // Store walking direction before falling/digging

    // Output registers
    reg r_walk_left, r_walk_right, r_aaah, r_digging;

    // State transition logic
    always @(*) begin
        next_state = current_state; // Default: stay in the same state
        case (current_state)
            WALK_LEFT: begin
                if (!ground) next_state = FALL;
                else if (dig) next_state = DIG;
                else if (bump_left || bump_right) next_state = WALK_RIGHT;
                else next_state = WALK_LEFT;
            end
            WALK_RIGHT: begin
                if (!ground) next_state = FALL;
                else if (dig) next_state = DIG;
                else if (bump_left || bump_right) next_state = WALK_LEFT;
                else next_state = WALK_RIGHT;
            end
            FALL: begin
                if (ground) next_state = (prev_walk_left) ? WALK_LEFT : WALK_RIGHT;
                else next_state = FALL;
            end
            DIG: begin
                if (!ground) next_state = FALL;
                else next_state = DIG;
            end
            default: next_state = WALK_LEFT;
        endcase
    end

    // State update
    always @(posedge clk or posedge areset) begin
        if (areset) begin
            current_state <= WALK_LEFT;
            prev_walk_left <= 1'b1;
        end else begin
            current_state <= next_state;
            if (current_state == WALK_LEFT && next_state == FALL) prev_walk_left <= 1'b1;
            if (current_state == WALK_RIGHT && next_state == FALL) prev_walk_left <= 1'b0;
            if (current_state == WALK_LEFT && next_state == DIG) prev_walk_left <= 1'b1;
            if (current_state == WALK_RIGHT && next_state == DIG) prev_walk_left <= 1'b0;
        end
    end

    // Output logic (Moore machine)
    always @(posedge clk or posedge areset) begin
        if (areset) begin
            r_walk_left <= 1'b1;
            r_walk_right <= 1'b0;
            r_aaah <= 1'b0;
            r_digging <= 1'b0;
        end else begin
            r_walk_left <= (current_state == WALK_LEFT);
            r_walk_right <= (current_state == WALK_RIGHT);
            r_aaah <= (current_state == FALL);
            r_digging <= (current_state == DIG);
        end
    end

    assign walk_left = r_walk_left;
    assign walk_right = r_walk_right;
    assign aaah = r_aaah;
    assign digging = r_digging;

endmodule

