module top_module (
    input clk,
    input reset,
    input s,
    input w,
    output reg z
);

    reg [2:0] state;
    parameter A = 3'b000, B = 3'b001, C = 3'b010, D = 3'b011, E = 3'b100;
    reg [1:0] count;

    always @(posedge clk) begin
        if (reset) begin
            state <= A;
            z <= 0;
            count <= 0;
        end else begin
            case (state)
                A: begin
                    if (s) begin
                        state <= B;
                        count <= 0;
                        z <= 0;
                    end else begin
                        state <= A;
                        z <= 0;
                    end
                end
                B: begin
                    state <= C;
                    count <= w ? 1 : 0;
                    z <= 0;
                end
                C: begin
                    state <= D;
                    count <= count + (w ? 1 : 0);
                    z <= 0;
                end
                D: begin
                    state <= E;
                    count <= count + (w ? 1 : 0);
                    z <= 0;
                end
                E: begin
                    state <= B;
                    z <= (count == 2'b10);
                    count <= 0;
                end
                default: state <= A;
            endcase
        end
    end

endmodule

