module top_module (
    input clk,
    input j,
    input k,
    input areset,
    output out
);

    // Define states
    localparam OFF = 1'b0;
    localparam ON  = 1'b1;

    // State register
    reg state;

    // Next state logic
    reg next_state;
    always @(*) begin
        case (state)
            OFF: begin
                if (j == 1'b1)
                    next_state = ON;
                else
                    next_state = OFF;
            end
            ON: begin
                if (k == 1'b1)
                    next_state = OFF;
                else
                    next_state = ON;
            end
            default: next_state = OFF; // Avoid latch
        endcase
    end

    // State update
    always @(posedge clk, posedge areset) begin
        if (areset)
            state <= OFF;
        else
            state <= next_state;
    end

    // Output logic
    assign out = state;

endmodule

