module top_module(
    input clk,
    input areset,

    input predict_valid,
    input [6:0] predict_pc,
    output reg predict_taken,
    output reg [6:0] predict_history,

    input train_valid,
    input train_taken,
    input train_mispredicted,
    input [6:0] train_history,
    input [6:0] train_pc
);

    reg [6:0] global_history;
    reg [1:0] pht [127:0];

    // Prediction Logic
    always @(*) begin
        if (predict_valid) begin
            // Calculate index
            integer index = predict_pc ^ global_history;
            predict_history = global_history;
            predict_taken = (pht[index] >= 2'b10); // Strongly or weakly taken
        end else begin
            predict_taken = 0; // Default prediction
            predict_history = global_history;
        end
    end

    // Training Logic
    always @(posedge clk) begin
        if (areset) begin
            global_history <= 7'b0;
            for (integer i = 0; i < 128; i = i + 1) begin
                pht[i] <= 2'b10; // Initialize to weakly taken
            end
        end else begin
            if (train_valid) begin
                // Calculate index
                integer index = train_pc ^ train_history;

                // Update PHT
                if (train_taken) begin
                    if (pht[index] < 2'b11) begin
                        pht[index] <= pht[index] + 1;
                    end
                end else begin
                    if (pht[index] > 2'b00) begin
                        pht[index] <= pht[index] - 1;
                    end
                end

                // Recover global history on misprediction
                if (train_mispredicted) begin
                    global_history <= train_history;
                end
            end

            // Update global history on prediction (if not training)
            if (predict_valid && !train_valid) begin
                if (predict_taken) begin
                    global_history <= {global_history[5:0], 1'b1};
                end else begin
                    global_history <= {global_history[5:0], 1'b0};
                end
            }
            // Update global history on training
            if (train_valid) begin
                if (train_taken) begin
                    global_history <= {train_history[5:0], 1'b1};
                end else begin
                    global_history <= {train_history[5:0], 1'b0};
                end
            }
        end
    end

endmodule

