module top_module(
    input clk,
    input reset,
    input data,
    output reg shift_ena,
    output reg counting,
    input done_counting,
    output reg done,
    input ack );

    reg [3:0] state;

    localparam IDLE = 4'b0000;
    localparam S1   = 4'b0001;
    localparam S2   = 4'b0010;
    localparam S3   = 4'b0011;
    localparam SHIFT = 4'b0100;
    localparam COUNT = 4'b0101;
    localparam DONE  = 4'b0110;

    reg [2:0] shift_count;

    always @(posedge clk) begin
        if (reset) begin
            state <= IDLE;
            shift_ena <= 0;
            counting <= 0;
            done <= 0;
            shift_count <= 0;
        end else begin
            case (state)
                IDLE: begin
                    if (data) state <= S1;
                    else state <= IDLE;
                    shift_ena <= 0;
                    counting <= 0;
                    done <= 0;
                end
                S1: begin
                    if (data) state <= S2;
                    else state <= IDLE;
                    shift_ena <= 0;
                    counting <= 0;
                    done <= 0;
                end
                S2: begin
                    if (~data) state <= S3;
                    else state <= IDLE;
                    shift_ena <= 0;
                    counting <= 0;
                    done <= 0;
                end
                S3: begin
                    if (data) begin
                        state <= SHIFT;
                        shift_ena <= 1;
                        shift_count <= 3;
                    end
                    else state <= IDLE;
                    counting <= 0;
                    done <= 0;
                end
                SHIFT: begin
                    if (shift_count > 0) begin
                        shift_ena <= 1;
                        shift_count <= shift_count - 1;
                        state <= SHIFT;
                    end else begin
                        shift_ena <= 0;
                        state <= COUNT;
                        counting <= 1;
                    end
                    done <= 0;
                end
                COUNT: begin
                    shift_ena <= 0;
                    counting <= 1;
                    if (done_counting) begin
                        state <= DONE;
                        counting <= 0;
                        done <= 1;
                    end
                end
                DONE: begin
                    shift_ena <= 0;
                    counting <= 0;
                    done <= 1;
                    if (ack) state <= IDLE;
                end
                default: state <= IDLE;
            endcase
        end
    end

endmodule

