  reg tc_reg;

  always @(posedge clk) begin
    if (load) begin
      counter <= data;
      tc_reg <= 0; // Reset tc when loading a new value
    } else begin
      if (counter > 0) begin
        counter <= counter - 1;
      end
      // Keep counter at 0 once it reaches 0.
    end
