    input j,
    input k,
    input reset,
    output out
);

    // Define the states
    localparam OFF = 1'b0;
    localparam ON  = 1'b1;

    // State register
    reg state;

    // Next state logic
    reg next_state;
    always @(*) begin
        case (state)
            OFF: begin
                if (j == 1'b1) begin
                    next_state = ON;
                end else begin
                    next_state = OFF;
