module top_module (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    input ground,
    input dig,
    output walk_left,
    output walk_right,
    output aaah,
    output digging
);

    // Define states
    typedef enum {WALK_LEFT, WALK_RIGHT, FALLING, DIGGING, SPLATTERED} state_t;
    reg state, next_state;

    // Falling counter
    reg [5:0] fall_count;

    // Walking direction register (used after falling/digging)
    reg walk_dir; // 0: left, 1: right

    // Output registers
    reg r_walk_left, r_walk_right, r_aaah, r_digging;

    // State transition logic
    always @(*) begin
        next_state = state;
        case (state)
            WALK_LEFT: begin
                if (!ground) begin
                    next_state = FALLING;
                    walk_dir = 0; // Remember direction
                end else if (dig) begin
                    next_state = DIGGING;
                end else if (bump_left || bump_right) begin
                    next_state = WALK_RIGHT;
                end
            end
            WALK_RIGHT: begin
                if (!ground) begin
                    next_state = FALLING;
                    walk_dir = 1; // Remember direction
                end else if (dig) begin
                    next_state = DIGGING;
                end else if (bump_left || bump_right) begin
                    next_state = WALK_LEFT;
                end
            end
            FALLING: begin
                if (ground) begin
                    if (fall_count > 20) begin
                        next_state = SPLATTERED;
                    end else begin
                        if (walk_dir == 0)
                            next_state = WALK_LEFT;
                        else
                            next_state = WALK_RIGHT;
                    end
                end
            end
            DIGGING: begin
                if (!ground) begin
                    next_state = FALLING;
                    walk_dir = (state == WALK_LEFT) ? 0 : 1; // Remember direction
                end
            end
            SPLATTERED: begin
                next_state = SPLATTERED; // Stay in splattered state
            end
            default: next_state = WALK_LEFT;
        endcase
    end

    // State update logic
    always @(posedge clk or posedge areset) begin
        if (areset) begin
            state <= WALK_LEFT;
            fall_count <= 0;
            walk_dir <= 0;
        end else begin
            state <= next_state;
            if (state == FALLING) begin
                if (!ground) begin
                    fall_count <= fall_count + 1;
                end else begin
                    fall_count <= 0;
                end
            end else begin
                fall_count <= 0;
            end
        end
    end

    // Output logic
    always @(*) begin
        r_walk_left = 0;
        r_walk_right = 0;
        r_aaah = 0;
        r_digging = 0;

        case (state)
            WALK_LEFT: r_walk_left = 1;
            WALK_RIGHT: r_walk_right = 1;
            FALLING: r_aaah = 1;
            DIGGING: r_digging = 1;
            SPLATTERED: begin
                r_walk_left = 0;
                r_walk_right = 0;
                r_aaah = 0;
                r_digging = 0;
            end
            default: r_walk_left = 1;
        endcase
    end

    assign walk_left = r_walk_left;
    assign walk_right = r_walk_right;
    assign aaah = r_aaah;
    assign digging = r_digging;

endmodule

