completion:
module top_module (
    input clk,
    input in,
    input reset,
    output out
);

  // Define states
  parameter A = 2'b00;
  parameter B = 2'b01;
  parameter C = 2'b10;
  parameter D = 2'b11;

  // State register
  reg [1:0] current_state, next_state;

  // Output register
  reg out;

  // State transition logic
  always @(posedge clk) begin
    if (reset) begin
      current_state <= A;
    end else begin
      current_state <= next_state;
    end
  end

  // Next state logic
  always @(*) begin
    case (current_state)
      A: begin
        if (in == 0) next_state = A;
        else next_state = B;
      end
      B: begin
        if (in == 0) next_state = C;
        else next_state = B;
      end
      C: begin
        if (in == 0) next_state = A;
        else next_state = D;
      end
      D: begin
        if (in == 0) next_state = C;
        else next_state = B;
      end
      default: next_state = A; //default to state A
    endcase
  end

  // Output logic
  always @(posedge clk) begin
    if (reset) begin
      out <= 0;
    end else begin
      case (current_state)
        D: out <= 1;
        default: out <= 0;
      endcase
    end
  end

endmodule

