module top_module (
    input clk,
    input reset,
    input s,
    input w,
    output reg z
);

    reg [1:0] state;
    localparam A = 2'b00, B = 2'b01, C = 2'b10, D = 2'b11;

    reg [1:0] w_history;

    always @(posedge clk) begin
        if (reset) begin
            state <= A;
            z <= 0;
            w_history <= 0;
        end else begin
            case (state)
                A: begin
                    if (s) begin
                        state <= B;
                        z <= 0;
                        w_history <= {w, w_history[1]};
                    end else begin
                        state <= A;
                        z <= 0;
                        w_history <= 0;
                    end
                end
                B: begin
                    state <= C;
                    w_history <= {w, w_history[1]};
                    z <= 0;
                end
                C: begin
                    state <= D;
                    w_history <= {w, w_history[1]};
                    z <= 0;
                end
                D: begin
                    state <= B;
                    w_history <= {w, w_history[1]};
                    if ((w_history[1] + w_history[0] + w) == 2)
                        z <= 1;
                    else
                        z <= 0;
                end
                default: begin
                    state <= A;
                    z <= 0;
                    w_history <= 0;
                end
            endcase
        end
    end

endmodule

