module top_module(
    input clk,
    input load,
    input [255:0] data,
    output reg [255:0] q);

    reg [255:0] next_q;

    always @(*) begin
        for (int i = 0; i < 256; i++) begin
            int row = i / 16;
            int col = i % 16;
            int neighbors = 0;

            // Calculate neighbors (toroidal)
            for (int dr = -1; dr <= 1; dr++) begin
                for (int dc = -1; dc <= 1; dc++) begin
                    if (dr == 0 && dc == 0) continue; // Skip self

                    int neighbor_row = (row + dr + 16) % 16;
                    int neighbor_col = (col + dc + 16) % 16;
                    int neighbor_index = neighbor_row * 16 + neighbor_col;

                    neighbors += q[neighbor_index];
                end
            end

            // Apply Game of Life rules
            if (neighbors <= 1)
                next_q[i] = 0;
            else if (neighbors == 2)
                next_q[i] = q[i];
            else if (neighbors == 3)
                next_q[i] = 1;
            else
                next_q[i] = 0;
        end
    end

    always @(posedge clk) begin
        if (load)
            q <= data;
        else
            q <= next_q;
    end

endmodule

