module top_module (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    input ground,
    input dig,
    output walk_left,
    output walk_right,
    output aaah,
    output digging
);

    // State definition
    parameter WALK_LEFT = 2'b00;
    parameter WALK_RIGHT = 2'b01;
    parameter FALL = 2'b10;
    parameter DIG = 2'b11;

    reg [1:0] current_state, next_state;

    // State transition logic
    always @(*) begin
        next_state = current_state; // Default: stay in the same state
        case (current_state)
            WALK_LEFT: begin
                if (!ground) next_state = FALL;
                else if (dig) next_state = DIG;
                else if (bump_left || bump_right) next_state = WALK_RIGHT;
                else next_state = WALK_LEFT;
            end
            WALK_RIGHT: begin
                if (!ground) next_state = FALL;
                else if (dig) next_state = DIG;
                else if (bump_left || bump_right) next_state = WALK_LEFT;
                else next_state = WALK_RIGHT;
