module top_module (
    input clk,
    input in,
    input reset,
    output out
);

    // Define the states
    localparam A = 1'b0;
    localparam B = 1'b1;

    // State register
    reg state;

    // Output register (Moore machine)
    reg out;

    always @(posedge clk) begin
        if (reset) begin
            state <= B;
        end else begin
            case (state)
                A: begin
                    if (in) begin
                        state <= A;
                    end else begin
