module top_module(
    input wire clk,
    input wire reset,
    input wire data,
    output wire [3:0] count,
    output reg counting,
    output reg done,
    input wire ack );

    reg [3:0] state;
    reg [3:0] delay;
    reg [11:0] cycle_count;
    reg [3:0] current_count;

    localparam IDLE = 0;
    localparam S1 = 1;
    localparam S2 = 2;
    localparam S3 = 3;
    localparam GET_DELAY0 = 4;
    localparam GET_DELAY1 = 5;
    localparam GET_DELAY2 = 6;
    localparam GET_DELAY3 = 7;
    localparam COUNTING = 8;
    localparam DONE = 9;

    always @(posedge clk) begin
        if (reset) begin
            state <= IDLE;
            counting <= 0;
            done <= 0;
            cycle_count <= 0;
            current_count <= 0;
            delay <= 0;
        end else begin
            case (state)
                IDLE: begin
                    if (data) state <= S1;
                end
                S1: begin
                    if (data) state <= S2;
                    else state <= IDLE;
                end
                S2: begin
                    if (!data) state <= S3;
                    else state <= IDLE;
                end
                S3: begin
                    if (data) state <= GET_DELAY0;
                    else state <= IDLE;
                end
                GET_DELAY0: begin
                    delay[3] <= data;
                    state <= GET_DELAY1;
                end
                GET_DELAY1: begin
                    delay[2] <= data;
                    state <= GET_DELAY2;
                end
                GET_DELAY2: begin
                    delay[1] <= data;
                    state <= GET_DELAY3;
                end
                GET_DELAY3: begin
                    delay[0] <= data;
                    state <= COUNTING;
                    counting <= 1;
                    current_count <= delay;
                    cycle_count <= 0;
                end
                COUNTING: begin
                    if (cycle_count < 999) begin
                        cycle_count <= cycle_count + 1;
                    end else begin
                        cycle_count <= 0;
                        if (current_count > 0) begin
                            current_count <= current_count - 1;
                        end else begin
                            counting <= 0;
                            done <= 1;
                            state <= DONE;
                        end
                    end
                end
                DONE: begin
                    if (ack) begin
                        done <= 0;
                        state <= IDLE;
                    end
                end
                default: state <= IDLE;
            endcase
        end
    end

    assign count = current_count;

endmodule

