module top_module(
    input clk,
    input load,
    input [255:0] data,
    output reg [255:0] q);

    reg [255:0] next_q;

    always @(*) begin
        for (int i = 0; i < 256; i++) begin
            int row = i / 16;
            int col = i % 16;
            int neighbors = 0;

            // Calculate neighbor indices with toroidal boundary conditions
            int north = (row == 0) ? 15 : row - 1;
            int south = (row == 15) ? 0 : row + 1;
            int west = (col == 0) ? 15 : col - 1;
            int east = (col == 15) ? 0 : col + 1;

            // Count live neighbors
            neighbors = neighbors + q[north * 16 + west];
            neighbors = neighbors + q[north * 16 + col];
            neighbors = neighbors + q[north * 16 + east];
            neighbors = neighbors + q[row * 16 + west];
            neighbors = neighbors + q[row * 16 + east];
            neighbors = neighbors + q[south * 16 + west];
            neighbors = neighbors + q[south * 16 + col];
            neighbors = neighbors + q[south * 16 + east];

            // Apply game rules
            if (neighbors <= 1)
                next_q[i] = 0;
            else if (neighbors == 2)
                next_q[i] = q[i];
            else if (neighbors == 3)
                next_q[i] = 1;
            else
                next_q[i] = 0;
        end
    end

    always @(posedge clk) begin
        if (load)
            q <= data;
        else
            q <= next_q;
    end

endmodule

