module top_module (
    input clk,
    input reset,
    input in,
    output reg disc,
    output reg flag,
    output reg err
);

  reg [3:0] state;

  localparam S_IDLE  = 4'b0000;
  localparam S_1     = 4'b0001;
  localparam S_11    = 4'b0010;
  localparam S_111   = 4'b0011;
  localparam S_1111  = 4'b0100;
  localparam S_11111 = 4'b0101;
  localparam S_111110 = 4'b0110;
  localparam S_111111 = 4'b0111;
  localparam S_ERR   = 4'b1000;

  always @(posedge clk) begin
    if (reset) begin
      state <= S_IDLE;
      disc <= 0;
      flag <= 0;
      err <= 0;
    end else begin
      disc <= 0;
      flag <= 0;
      err <= 0;

      case (state)
        S_IDLE: begin
          if (in) state <= S_1;
          else state <= S_IDLE;
        end
        S_1: begin
          if (in) state <= S_11;
          else state <= S_IDLE;
        end
        S_11: begin
          if (in) state <= S_111;
          else state <= S_IDLE;
        end
        S_111: begin
          if (in) state <= S_1111;
          else state <= S_IDLE;
        end
        S_1111: begin
          if (in) state <= S_11111;
          else state <= S_IDLE;
        end
        S_11111: begin
          if (in) state <= S_111111;
          else begin
            state <= S_111110;
            disc <= 1;
          end
        end
        S_111110: begin
          if (in) state <= S_1;
          else state <= S_IDLE;
        end
        S_111111: begin
          if (in) begin
            state <= S_ERR;
            err <= 1;
          end else begin
            state <= S_IDLE;
            flag <= 1;
          end
        end
        S_ERR: begin
          if (in) begin
            state <= S_ERR;
            err <= 1;
          end else state <= S_IDLE;
        end
        default: state <= S_IDLE;
      endcase
    end
  end

endmodule

