module top_module(
    input wire clk,
    input wire reset,
    input wire data,
    output wire [3:0] count,
    output reg counting,
    output reg done,
    input wire ack );

    reg [3:0] state;
    reg [3:0] delay;
    reg [11:0] cycle_count;
    reg [3:0] current_count;

    localparam IDLE = 0;
    localparam DETECT1 = 1;
    localparam DETECT2 = 2;
    localparam DETECT3 = 3;
    localparam GET_DELAY0 = 4;
    localparam GET_DELAY1 = 5;
    localparam GET_DELAY2 = 6;
    localparam GET_DELAY3 = 7;
    localparam COUNTING = 8;
    localparam DONE = 9;

    assign count = current_count;

    always @(posedge clk) begin
        if (reset) begin
            state <= IDLE;
            counting <= 0;
            done <= 0;
            cycle_count <= 0;
            current_count <= 0;
            delay <= 0;
        end else begin
            case (state)
                IDLE: begin
                    if (data) state <= DETECT1;
                    else state <= IDLE;
                    counting <= 0;
                    done <= 0;
                end
                DETECT1: begin
                    if (data) state <= DETECT2;
                    else state <= IDLE;
                end
                DETECT2: begin
                    if (~data) state <= DETECT3;
                    else state <= DETECT1;
                end
                DETECT3: begin
                    if (data) state <= GET_DELAY0;
                    else state <= IDLE;
                end
                GET_DELAY0: begin
