            end
            default: next_state = WALK_LEFT;
        endcase
    end

    // State register update
    always @(posedge clk or posedge areset) begin
        if (areset) begin
            state <= WALK_LEFT;
            fall_count <= 0;
            walk_dir <= 0;
        end else begin
            state <= next_state;
        end
