    always @(posedge clk or posedge areset) begin
        if (areset) begin
            fall_count <= 0;
        end else if (state == FALLING) begin
            fall_count <= fall_count + 1;
        end else begin
            fall_count <= 0;
        end
    end

    // Output logic
    always @(*) begin
        r_walk_left = 0;
        r_walk_right = 0;
        r_aaah = 0;
        r_digging = 0;

