module top_module(
    input clk,
    input areset,

    input predict_valid,
    input [6:0] predict_pc,
    output reg predict_taken,
    output reg [6:0] predict_history,

    input train_valid,
    input train_taken,
    input train_mispredicted,
    input [6:0] train_history,
    input [6:0] train_pc
);

    reg [6:0] global_history;
    reg [1:0] pht [127:0];

    // Prediction logic
    always @(*) begin
        integer index;
        index = predict_pc ^ global_history;
        predict_history = global_history; // Output the current history
        predict_taken = (pht[index] >= 2'b10); // Predict taken if counter >= 2
    end

    // Training logic
    always @(posedge clk) begin
        if (areset) begin
            global_history <= 7'b0;
            for (integer i = 0; i < 128; i = i + 1) begin
                pht[i] <= 2'b10; // Initialize to weakly taken
            end
        end else begin
            if (train_valid) begin
                integer train_index;
                train_index = train_pc ^ train_history;

                // Update PHT
                if (train_taken) begin
                    if (pht[train_index] < 2'b11) begin
                        pht[train_index] <= pht[train_index] + 1'b1;
                    end
                end else begin
                    if (pht[train_index] > 2'b00) begin
                        pht[train_index] <= pht[train_index] - 1'b1;
                    end
