module top_module (
    input clk,
    input areset,
    input bump_left,
    input bump_right,
    input ground,
    input dig,
    output walk_left,
    output walk_right,
    output aaah,
    output digging
);

    // Define states
    typedef enum {WALK_LEFT, WALK_RIGHT, FALLING, DIGGING, SPLATTERED} state_t;
    reg state, next_state;

    // Falling counter
    reg [5:0] fall_count;
    reg [5:0] next_fall_count;

    // Walking direction register (used after falling/digging)
    reg walk_dir; // 0: left, 1: right
    reg next_walk_dir;

    // Output registers
    reg r_walk_left, r_walk_right, r_aaah, r_digging;

    // State transition logic
    always @(*) begin
        next_state = state;
        next_fall_count = fall_count;
        next_walk_dir = walk_dir;
        r_walk_left = 0;
        r_walk_right = 0;
        r_aaah = 0;
        r_digging = 0;

        case (state)
            WALK_LEFT: begin
                r_walk_left = 1;
                if (!ground) begin
                    next_state = FALLING;
                    next_fall_count = 0;
                    next_walk_dir = 0; // Remember direction before falling
                    r_aaah = 1;
                end else if (dig) begin
                    next_state = DIGGING;
                    r_digging = 1;
                end else if (bump_left || bump_right) begin
                    next_state = WALK_RIGHT;
                    next_walk_dir = 1;
                end
            end
            WALK_RIGHT: begin
                r_walk_right = 1;
                if (!ground) begin
                    next_state = FALLING;
                    next_fall_count = 0;
                    next_walk_dir = 1; // Remember direction before falling
                    r_aaah = 1;
                end else if (dig) begin
                    next_state = DIGGING;
                    r_digging = 1;
                end else if (bump_left || bump_right) begin
                    next_state = WALK_LEFT;
                    next_walk_dir = 0;
                end
            end
            FALLING: begin
                r_aaah = 1;
                next_fall_count = fall_count + 1;
                if (ground) begin
                    if (fall_count > 20) begin
                        next_state = SPLATTERED;
                    end else begin
                        if (walk_dir == 0)
                            next_state = WALK_LEFT;
                        else
                            next_state = WALK_RIGHT;
                    end
                end
            end
            DIGGING: begin
                r_digging = 1;
                if (!ground) begin
                    next_state = FALLING;
                    next_fall_count = 0;
                    r_aaah = 1;
                end
            end
            SPLATTERED: begin
                r_walk_left = 0;
                r_walk_right = 0;
                r_aaah = 0;
                r_digging = 0;
            end
            default: next_state = WALK_LEFT;
        endcase
    end

    // State update logic
    always @(posedge clk, posedge areset) begin
        if (areset) begin
            state <= WALK_LEFT;
            fall_count <= 0;
            walk_dir <= 0;
        end else begin
            state <= next_state;
            fall_count <= next_fall_count;
            walk_dir <= next_walk_dir;
        end
    end

