reg [3:0] state; // 4 states (idle, wait, process, done)
reg [7:0] counter; // Counter for number of clock cycles

// Inputs and outputs
input clk, reset; // Clock and reset signals
input data; // Data input
output valid, out; // Valid output and data output

// Module body
always @(*)
begin
    case (state)
        // Idle state
        default: begin
            if (reset)
            begin
                state <= 0;
                counter <= 0;
            end else if (clk)
            begin
                state <= state + 1;
                counter <= counter + 1;
                if (counter >= 8) // Threshold for transition to process state
                begin
                    state <= 1; // Transition to process state
                    counter <= 0; // Reset counter
                end
            end
        end

        // Wait state
        2: begin
            if (reset)
            begin
                state <= 0;
                counter <= 0;
            end else if (clk)
            begin
                out <= data; // Output the processed data
                valid <= 1; // Set valid output to 1
                state <= 3; // Transition to done state
            end
        end

        // Process state
        3: begin
            if (reset)
            begin
                state <= 0;
                counter <= 0;
            end else if (clk)
            begin
                out <= data; // Output the processed data
                valid <= 1; // Set valid output to 1
                state <= 4; // Transition back to idle state
            end
        end

        // Done state
        4: begin
            if (reset)
            begin
                state <= 0;
                counter <= 0;
            end else if (clk)
            begin
                valid <= 1; // Set valid output to 1
                out <= data; // Output the processed data
            end
        end
    endcase
end
