    input   areset,
    input   x,
    output z
) {
  // Define the two states of the state machine
  parameter A = 0;
  parameter B = 1;

  // State register
  reg [1:0] state;

  // Clock signal
  always @(posedge clk or negedge areset) begin
    // Reset the state on asynchronous reset
    if (areset) begin
      state <= A;
    end else begin
      // Set the state based on the current x input value
      case (x) is
        // State is A and x = 1
        when 1 => state <= B;
        // State is B and x = 0
        when 0 => state <= A;
        // Otherwise, keep the current state
        default: state <= state;
      endcase
    end
  end

  // Output z is set to 1 when the state is A and the x input is 1, or when the state is B and the x input is 0
  always @* begin
    if (state == A && x == 1) begin
      z <= 1;
    end else if (state == B && x == 0) begin
      z <= 1;
    end else begin
      z <= 0;
    end
  end
}
endmodule

