module top_module (
    input clk,
    input reset,
    input data,
    output start_shifting) {

  // Define the states of the state machine
  localparam S = 0;
  localparam S1 = 1;
  localparam S11 = 2;
  localparam S110 = 3;
  localparam Done = 4;

  // Create a register to store the current state of the state machine
  reg [1:0] state = S;

  // Combinational logic block to determine the next state based on the current state and data input
  comb {
    if (state === S && data) begin
      state <= S1;
    end else if (state === S && !data) begin
      state <= S;
    end else if (state === S1 && data) begin
      state <= S11;
    end else if (state === S1 && !data) begin
