    end else if (state === S11 && data) begin
      state <= S110;
    end else if (state === S11 && !data) begin
      state <= S1;
    end else if (state === Done) begin
      state <= Done;
    end
  }

  // Output the next state of the state machine based on the current state and data input
  assign start_shifting = state === Done;

  // Reset the state machine to the initial state on the rising edge of the reset signal
  always @(posedge clk) begin
    if (reset) begin
      state <= S;
    end
  end
}
