    reg [31:0] q_next; // Next value of q register
    reg [31:0] q; // Current value of q register

    // Configure input ports
    assign q = q_next; // Update q register on rising edge of clk
    always @(posedge clk or negedge reset) begin
        if (reset) q_next <= 32'h1; // Reset q_next to initial value
        else q_next <= q; // Shift q_next by 1 bit
