    process(clk, reset, data)
    begin
        if (reset)
            state <= S;
        else if (data && state == S)
            state <= S1;
        else if (data && state == S1)
            state <= S11;
        else if (data && state == S11)
            state <= S110;
        else if (!data && state == Done)
            state <= Done;
    end process;

    // Define output assignment based on current state
    assign start_shifting = state == Done;
endmodule

