    // Reset register on reset signal
    if (reset) q <= 32'h1;
    // Update q register on rising edge of clock
    else q <= q_next;
    // Update q_next register on every clock cycle
    q_next <= q [31:0] << 1;
  end

  // Connect output register to input port
  assign q = q;
}
endmodule

