module top_module(
    input d,
    input done_counting,
    input ack,
    input [9:0] state,
    output B3_next,
    output S_next,
    output S1_next,
    output Count_next,
    output Wait_next,
    output done,
    output counting,
    output shift_ena
) :
    // State machine logic
    always@(state or done_counting or ack) begin
        case (state)
            S: B3_next = 1; S_next = 1; S1_next = 0; Count_next = 0; Wait_next = 0;
            S1: B3_next = 0; S_next = 0; S1_next = 1; Count_next = 0; Wait_next = 0;
            S11: B3_next = 0; S_next = 0; S1_next = 0; Count_next = 0; Wait_next = 0;
            B0: B3_next = 0; S_next = 1; S1_next = 0; Count_next = 0; Wait_next = 0;
            B1: B3_next = 0; S_next = 1; S1_next = 0; Count_next = 0; Wait_next = 0;
            B2: B3_next = 1; S_next = 0; S1_next = 0; Count_next = 0; Wait_next = 0;
            Count: B3_next = 0; S_next = 0; S1_next = 0; Count_next = 1; Wait_next = 0;
            Wait: B3_next = 0; S_next = 0; S1_next = 0; Count_next = 0; Wait_next = 1;
        endcase
    end

    // Shift ena signal
    always@(B2 or B3 or B1 or B0) begin
        shift_ena = (B2 & B3) | (B1 & B0);
    end

endmodule
