reg [7:0] count;
reg [7:0] input_reg;
reg [7:0] output_reg;

localparam IDLE = 1'b0;
localparam ACTIVE = 1'b1;

always @(posedge clk or posedge areset) begin
    if (areset) begin
        state <= IDLE;
        input_reg <= 8'b0;
        output_reg <= 8'b0;
