                input_reg[count] <= x;
                count <= count + 1;
                if (count == 8) begin
                    state <= IDLE;
                    output_reg <= ~input_reg;
                end
            end
        endcase
    end
end

assign z = (state == ACTIVE) ? output_reg[count-1] : 1'b0;

