module top_module (
    input clk,
    input [7:0] in,
    input reset,
    output [23:0] out_bytes,
    output done
);

reg [2:0] state;
reg [23:0] message;
reg done_reg;

localparam IDLE = 3'b000,
           WAIT_BYTE1 = 3'b001,
           WAIT_BYTE2 = 3'b010,
           WAIT_BYTE3 = 3'b011,
           MESSAGE_RECEIVED = 3'b100;

always @(posedge clk or posedge reset) begin
    if (reset) begin
        state <= IDLE;
        message <= 24'b0;
        done_reg <= 1'b0;
    end else begin
        case (state)
            IDLE: begin
                if (in[3]) begin
                    state <= WAIT_BYTE1;
                    message[23:16] <= in;
                    done_reg <= 1'b0;
                end
            end
            WAIT_BYTE1: begin
                message[15:8] <= in;
                state <= WAIT_BYTE2;
            end
            WAIT_BYTE2: begin
                message[7:0] <= in;
                state <= WAIT_BYTE3;
            end
            WAIT_BYTE3: begin
                if (in[3]) begin
                    state <= IDLE;
                    done_reg <= 1'b1;
                end else begin
                    message[23:16] <= message[15:8];
                    message[15:8] <= message[7:0];
                    message[7:0] <= in;
                end
            end
            MESSAGE_RECEIVED: begin
                if (~in[3]) begin
                    state <= IDLE;
                    done_reg <= 1'b0;
                end
            end
            default: state <= IDLE;
        endcase
    end
end

assign out_bytes = done_reg ? message : 24'bx;
assign done = done_reg;

endmodule

