    localparam WALK_LEFT = 1'b0;
    localparam WALK_RIGHT = 1'b1;

    reg state, next_state;

    // State register
    always @(posedge clk or posedge areset) begin
        if (areset)
            state <= WALK_LEFT;
        else
            state <= next_state;
