    input clk,
    input j,
    input k,
    input reset,
    output out
);

    // Declare the state register
    reg [1:0] state;

    // Declare the next state variable
    reg [1:0] next_state;

    // Declare the output variable
    reg out_reg;

    // State definitions
    localparam OFF = 2'b00;
    localparam ON = 2'b01;

    // State register
    always @(posedge clk) begin
        if (reset) begin
            state <= OFF;
        end else begin
            state <= next_state;
